__NUXT_JSONP__("/books/book_1", (function(a){return {data:[{book:{slug:a,id:a,title:"Operating System Concepts",copyright:2012,language:"ENGLISH",pages:976,authorId:"author_1",publisherId:"publisher_1",image:"books\u002Fbook_1.jpg",edition:"9th",toc:[],body:{type:"root",children:[{type:"element",tag:"p",props:{},children:[{type:"text",value:"Operating System Concepts, now in its ninth edition, continues to provide a solid theoretical foundation for understanding operating systems. The ninth edition has been thoroughly updated to include contemporary examples of how operating systems function. The text includes content to bridge the gap between concepts and actual implementations. End-of-chapter problems, exercises, review questions, and programming exercises help to further reinforce important concepts.  A new Virtual Machine provides interactive exercises to help engage students with the material."}]}]},dir:"\u002Fbooks",path:"\u002Fbooks\u002Fbook_1",extension:".md",createdAt:"2023-09-23T19:29:34.900Z",updatedAt:"2023-08-14T21:43:32.370Z"},author:[{name:"Abraham Silberschatz"}],publisher:[{name:"John Wiley & Sons"}]}],fetch:{},mutations:void 0}}("book_1")));