__NUXT_JSONP__("/books/book_5", (function(a){return {data:[{book:{slug:a,id:a,title:"Compilers, Principles, Techniques, and Tools",edition:"2nd",copyright:2006,language:"ENGLISH",pages:1040,authorId:"author_3",publisherId:"publisher_3",image:"books\u002Fbook_5.jpg",toc:[],body:{type:"root",children:[{type:"element",tag:"p",props:{},children:[{type:"text",value:"Compilers: Principles, Techniques and Tools, known to professors, students, and developers worldwide as the \"Dragon Book,\" is available in a new edition.  Every chapter has been completely revised to reflect developments in software engineering, programming languages, and computer architecture that have occurred since 1986, when the last edition published.  The authors, recognizing that few readers will ever go on to construct a compiler, retain their focus on the broader set of problems faced in software design and software development."}]}]},dir:"\u002Fbooks",path:"\u002Fbooks\u002Fbook_5",extension:".md",createdAt:"2023-09-23T19:29:34.905Z",updatedAt:"2023-08-14T21:43:32.370Z"},author:[{name:"Alfred V. Aho"}],publisher:[{name:"Addison Wesley"}]}],fetch:{},mutations:void 0}}("book_5")));