__NUXT_JSONP__("/publishers/publisher_3", (function(a,b,c,d,e,f,g){return {data:[{publisher:{slug:b,id:b,name:"Addison Wesley",country:"United States",founded:1942,genere:["Computer Science"],image:"publishers\u002Fpublisher_3.png",toc:[],body:{type:e,children:[{type:c,tag:d,props:{},children:[{type:a,value:"Addison-Wesley is an American publisher of textbooks and computer literature. It is an imprint of Pearson PLC, a global publishing and education company. In addition to publishing books, Addison-Wesley also distributes its technical titles through the Safari Books Online e-reference service. Addison-Wesley's majority of sales derive from the United States (55%) and Europe (22%).[1]"}]},{type:a,value:"\n"},{type:c,tag:d,props:{},children:[{type:a,value:"The Addison-Wesley Professional Imprint produces content including books, eBooks, and video for the professional IT worker including developers, programmers, managers, system administrators. Classic titles include The Art of Computer Programming, The C++ Programming Language, The Mythical Man-Month, and Design Patterns. Addison-Wesley Professional is also a partner with Safari Books Online."}]}]},dir:"\u002Fpublishers",path:"\u002Fpublishers\u002Fpublisher_3",extension:f,createdAt:"2023-09-23T19:29:34.913Z",updatedAt:"2023-08-14T21:43:32.372Z"},books:[{slug:g,id:g,title:"Compilers, Principles, Techniques, and Tools",edition:"2nd",copyright:2006,language:"ENGLISH",pages:1040,authorId:"author_3",publisherId:b,image:"books\u002Fbook_5.jpg",toc:[],body:{type:e,children:[{type:c,tag:d,props:{},children:[{type:a,value:"Compilers: Principles, Techniques and Tools, known to professors, students, and developers worldwide as the \"Dragon Book,\" is available in a new edition.  Every chapter has been completely revised to reflect developments in software engineering, programming languages, and computer architecture that have occurred since 1986, when the last edition published.  The authors, recognizing that few readers will ever go on to construct a compiler, retain their focus on the broader set of problems faced in software design and software development."}]}]},dir:"\u002Fbooks",path:"\u002Fbooks\u002Fbook_5",extension:f,createdAt:"2023-09-23T19:29:34.905Z",updatedAt:"2023-08-14T21:43:32.370Z"}]}],fetch:{},mutations:void 0}}("text","publisher_3","element","p","root",".md","book_5")));