__NUXT_JSONP__("/publishers_index", (function(a,b,c,d,e,f,g,h,i,j,k){return {data:[{publishers:[{slug:g,id:g,name:"John Wiley & Sons",country:h,founded:1807,genere:["Academic"],image:"publishers\u002Fpublisher_1.png",toc:[],body:{type:d,children:[{type:b,tag:c,props:{},children:[{type:a,value:"John Wiley & Sons, Inc., commonly known as Wiley, is an American multinational publishing company founded in 1807 that focuses on academic publishing and instructional materials. The company produces books, journals, and encyclopedias, in print and electronically, as well as online products and services,[1] training materials, and educational materials for undergraduate, graduate, and continuing education students."}]}]},dir:e,path:"\u002Fpublishers\u002Fpublisher_1",extension:f,createdAt:"2023-09-23T19:29:34.911Z",updatedAt:i},{slug:j,id:j,name:"Pearson Education",country:"United Kingdom",founded:1844,genere:["Education"],image:"publishers\u002Fpublisher_2.png",toc:[],body:{type:d,children:[{type:b,tag:c,props:{},children:[{type:a,value:"Pearson Education is a British-owned education publishing and assessment service to schools and corporations, as well for students directly. Pearson owns educational media brands including Addisonâ€“Wesley, Peachpit, Prentice Hall, eCollege, Longman, Scott Foresman, and others. Pearson is part of Pearson plc, which formerly owned the Financial Times. It claims to have been formed in 1840,[1] with the current incarnation of the company created when Pearson plc purchased the education division of Simon & Schuster (including Prentice Hall and Allyn & Bacon) from Viacom and merged it with its own education division, Addison-Wesley Longman, to form Pearson Education. Pearson Education was rebranded to Pearson in 2011 and split into an International and a North American division."}]}]},dir:e,path:"\u002Fpublishers\u002Fpublisher_2",extension:f,createdAt:"2023-09-23T19:29:34.912Z",updatedAt:i},{slug:k,id:k,name:"Addison Wesley",country:h,founded:1942,genere:["Computer Science"],image:"publishers\u002Fpublisher_3.png",toc:[],body:{type:d,children:[{type:b,tag:c,props:{},children:[{type:a,value:"Addison-Wesley is an American publisher of textbooks and computer literature. It is an imprint of Pearson PLC, a global publishing and education company. In addition to publishing books, Addison-Wesley also distributes its technical titles through the Safari Books Online e-reference service. Addison-Wesley's majority of sales derive from the United States (55%) and Europe (22%).[1]"}]},{type:a,value:"\n"},{type:b,tag:c,props:{},children:[{type:a,value:"The Addison-Wesley Professional Imprint produces content including books, eBooks, and video for the professional IT worker including developers, programmers, managers, system administrators. Classic titles include The Art of Computer Programming, The C++ Programming Language, The Mythical Man-Month, and Design Patterns. Addison-Wesley Professional is also a partner with Safari Books Online."}]}]},dir:e,path:"\u002Fpublishers\u002Fpublisher_3",extension:f,createdAt:"2023-09-23T19:29:34.913Z",updatedAt:"2023-08-14T21:43:32.372Z"}]}],fetch:{},mutations:void 0}}("text","element","p","root","\u002Fpublishers",".md","publisher_1","United States","2023-08-14T21:43:32.371Z","publisher_2","publisher_3")));